import { Button, Checkbox, Form, Input, Space } from 'antd';
import React, { useState } from 'react';

const CreateSurvey = () => {

  const [survey, setSurvey] = useState([
    {
      "id" : 1,
      "questionType": "MCQ",
      "answerList": [
        {
          "id": 1,
          "description": "0-20"
        },
        {
          "id": 2,
          "description": "21-30"
        },
        {
          "id": 3,
          "description": "31-40"
        },
        {
          "id": 4,
          "description": ""
        },
        {
          "id": 5,
          "description": ""
        }
      ],
      "description": "What is your age range?"
    },
    {
      "id":2,
      "questionType": "MCQ",
      "answerList": [
        {
          "id": 1,
          "description": "ADL"
        },
        {
          "id": 2,
          "description": "Dialog"
        },
        {
          "id": 3,
          "description": "Mobitel"
        },
        {
          "id": 4,
          "description": "Ncell"
        },
        {
          "id": 5,
          "description": "XL"
        }
      ],
      "description": "What is your company name?"
    },
    {
      "id":3,
      "questionType": "OPEN_ENDED",
      "answerList": null,
      "description": "What is your company name?"
    }
  ])
  const onFinish = (values) => {
    console.log('Success:', values);
  };

  const onFinishFailed = (errorInfo) => {
    console.log('Failed:', errorInfo);
  };


  return (
    <Form.Provider
      onFormFinish={(name, { values, forms }) => {
        if (name === 'userForm') {
          const { basicForm } = forms;
          const users = basicForm.getFieldValue('users') || [];
          basicForm.setFieldsValue({
            users: [...users, values],
          });
          setVisible(false);
        }
      }}
    >

   
    <Form
      name="basic"
      labelCol={{
        span: 8,
      }}
      wrapperCol={{
        span: 16,
      }}
      initialValues={{
        remember: true,
      }}
      onFinish={onFinish}
      onFinishFailed={onFinishFailed}
      autoComplete="off"
    >

      {survey.length ?

        survey.map((question) => {

          return (
            <>
              
              
                  <Form.Item
                    label="Description"
                    name={question.id}

                    rules={[
                      {
                        required: false,
                        message: 'Please input your username!',
                      },
                    ]}
                  >
                    <Input />
                  </Form.Item>
                  

                  {question.questionType === "MCQ" ?
                    question.answerList.map((answer) => (
                      <Form.Item
                        label={answer.id}
                        name={answer.id}
                        
                        rules={[
                          {
                            required: false,
                            message: 'Please input your username!',
                          },
                        ]}
                      >
                        <Input />
                      </Form.Item>))
                    : null}
           
              
            </>
          )
        }


        )



        : 'button'}





      <Form.Item
        wrapperCol={{
          offset: 8,
          span: 16,
        }}
      >
        <Button type="primary" htmlType="submit">
          Submit
        </Button>
      </Form.Item>
    </Form>
    </Form.Provider>
  );
};

export default CreateSurvey;
