import { Button, Checkbox, Form, Input } from 'antd';
import React, { useState } from 'react';

const CreateSurvey = () => {

  const [survey, setSurvey] = useState([
    {
      "questionType": "MCQ",
      "answerList": [
        {
          "id": 1,
          "description": "0-20"
        },
        {
          "id": 2,
          "description": "21-30"
        },
        {
          "id": 3,
          "description": "31-40"
        },
        {
          "id": 4,
          "description": ""
        },
        {
          "id": 5,
          "description": ""
        }
      ],
      "description": "What is your age range?"
    },
    {
      "questionType": "MCQ",
      "answerList": [
        {
          "id": 1,
          "description": "ADL"
        },
        {
          "id": 2,
          "description": "Dialog"
        },
        {
          "id": 3,
          "description": "Mobitel"
        },
        {
          "id": 4,
          "description": "Ncell"
        },
        {
          "id": 5,
          "description": "XL"
        }
      ],
      "description": "What is your company name?"
    },
    {
      "questionType": "OPEN_ENDED",
      "answerList": null,
      "description": "What is your company name?"
    }
  ])
  const onFinish = (values) => {
    console.log('Success:', values);
  };

  const onFinishFailed = (errorInfo) => {
    console.log('Failed:', errorInfo);
  };


  return (
    <Form
      name="basic"
      labelCol={{
        span: 8,
      }}
      wrapperCol={{
        span: 16,
      }}
      initialValues={{
        remember: true,
      }}
      onFinish={onFinish}
      onFinishFailed={onFinishFailed}
      autoComplete="off"
    >

      {survey.length ?

        survey.map((question) => {
          if (question.questionType === "OPEN_ENDED") {
            return (
            <Form.Item
              label="Description"
              name="description"
              initialValue={question.description}
              rules={[
                {
                  required: true,
                  message: 'Please input your username!',
                },
              ]}
            >
              <Input />
            </Form.Item>
            )
          }
          else return (null)
        })

        

        : 'button'}





      <Form.Item
        wrapperCol={{
          offset: 8,
          span: 16,
        }}
      >
        <Button type="primary" htmlType="submit">
          Submit
        </Button>
      </Form.Item>
    </Form>
  );
};

export default CreateSurvey;
